import express from 'express';
import checkCode from '../controllers/checkCode';
import createVisitor from '../controllers/createVisitor';
import checkOut from '../controllers/checkOut';
import bookWalkIn from '../controllers/bookWalkIn';
import checkNumber from '../controllers/checkNumber';
import auth from '../middleware/auth';

import getCheckIns from '../controllers/getCheckIns';

const router = express();



router.post('/saveWalkIn', bookWalkIn);
router.post('/verify-code', checkCode);
router.post('/check-out', checkOut);
router.post('/checkNumber', checkNumber);
router.post('/createVisitor', auth, createVisitor);
router.get('/fetchCheckIns', auth, getCheckIns);


module.exports = router;

