sourcesContent:
import { visitorModel } from "../middleware/dbConnect";
import { verifyToken } from "../utills/otp";
import { StatusCodes } from "http-status-codes";


const checkCode = async (req, res) => {
    const { vCode, service_sid, phoneNo } = req.body;
        try {

            console.log(req.body)
                verifyToken(phoneNo, vCode, service_sid ).then((verificationCode) => {
            console.log(verificationCode)

                    if(verificationCode.status === "approved"){
                        res.status(StatusCodes.OK).json({
                            message: "Visitor verified",
                            status: StatusCodes.OK,
                            success: true,
                        })
                    }else{
                        res.status(StatusCodes.BAD_REQUEST).json({
                            message: "Invalid code",
                            status: StatusCodes.BAD_REQUEST,
                            success: true,
                        })
                    }
                }) 
        }catch (error) {
            // console.log(error)
            res.status(500).json({
                message: "Error sending SMS",
                data: error
            });
        }
};

export default checkCode;

