sourcesContent:
import { DBConnection } from "../config/db";

import visitorSchema from "../models/visitor";
import adminSchema from '../models/admin';
import employeeSchema from '../models/employee';
import rolesSchema from "../models/roles";
import departmentsSchema from "../models/departments";


//visitors db model
const visitorModel = DBConnection.model("Visitor", visitorSchema);
const adminModel = DBConnection.model("admin", adminSchema)
const employeeModel = DBConnection.model("employee", employeeSchema);
const rolesModel = DBConnection.model("roles", rolesSchema);
const departmentsModel = DBConnection.model("departments", departmentsSchema);




module.exports = {
    visitorModel,
    adminModel,
    employeeModel,
    rolesModel,
    departmentsModel
};

