    "enum": ["Pre-booked", "Walk-in"],
    "default": "Walk-in"
  },
  visitingDate: {
    type: String
  },
  checkInTime: {
    type: String
  },
  checkOutTime: {
    type: String
  },
  logOut: {
    type: Boolean,
    "default": false
  },
  isDeleted: {
    type: Boolean,
    "default": false
  },
  createdAt: {
    type: Date,
    "default": new Date()
  },
  updatedAt: {
    type: Date,
    "default": new Date()
  },
  deletedAt: Date
});
visitorSchema.plugin(mongoosePaginate);
module.exports = visitorSchema;
