const mongoose = require("mongoose");
const mongoosePaginate = require("mongoose-paginate-v2");
const Schema = mongoose.Schema

const visitorSchema = new Schema({
    name:{
        type: String,
        required: true
    },
    email:{
        type: String,
        required: true
    },
    companyId:{
        type: Schema.Types.ObjectId,
        ref: "admins",
        required: true
    },
    phoneNo: {
        type: String,
        required: true 
    },
    visitingPurpose: {
        type: String,
        required: true
    },
    visitorStatus:{
        type: String,
        enum: ["Pre-booked","Walk-in"],
        default: "Walk-in"
    },
    visitingDate: {
        type: String,
    },
    checkInTime: {
        type: String,
    },
    checkOutTime: {
        type: String,
    },
    logOut: {
        type: Boolean, 
        default: false
    },

    isDeleted: { 
        type: Boolean, 
        default: false 
    },
    createdAt: { 
        type: Date, 
        default: new Date() 
    },
    updatedAt: { 
        type: Date, 
        default: new Date() 
    },
    deletedAt: Date
    },

);

visitorSchema.plugin(mongoosePaginate);

module.exports = visitorSchema;

