import { visitorModel } from "../middleware/dbConnect";
import { sendOTP } from "../utills/otp";
import { StatusCodes } from "http-status-codes";



const createVisitor = async (req, res) => {
    const { name, email, phoneNo, visitingPurpose, visitingDate} = req.body;
    let company = req.user.id;

    const convertVisitingDate = new Date(visitingDate).toDateString();
    let option = {
        phoneNo: phoneNo,
        visitingDate: convertVisitingDate,
      };

    try {
        //check if visitor has been booked already
        const checkVisitor = await visitorModel.find(option).exec();
        if(checkVisitor.length > 0){
                res.status(StatusCodes.BAD_REQUEST).json({
                    message: "Visitor has already been booked for this date on the system",
                    status: StatusCodes.BAD_REQUEST,
                    success: true,
                })
                return;
        }else{
            // insert data into the db
            const saveVisitor = await new visitorModel({
                name: name, 
                email: email,
                companyId: company,
                phoneNo : phoneNo,
                visitingPurpose: visitingPurpose,
                visitorStatus: "Pre-booked",
                visitingDate: convertVisitingDate
            }).save();
            res.status(StatusCodes.OK).json({
                message: "Visitor has been booked successfully",
                status: StatusCodes.OK,
                success: true,
                data: saveVisitor
            })
        }       

    } catch (error) {
        res.status(500).json({ error: error.message });
    }
}

export default createVisitor;

