sourcesContent:
import { visitorModel } from "../middleware/dbConnect";
import { StatusCodes  } from "http-status-codes";


const checkOut = async (req, res)=>{
    const { phoneNo} = req.body

    let option = {
        phoneNo: phoneNo,
        visitingDate: new Date().toDateString(),
        logOut: false
    }

    try {
        const checkVisitor = await visitorModel.find(option).exec();
        if(checkVisitor.length > 0){
            await visitorModel.updateOne(option, {
                checkOutTime: new Date().toTimeString(),
                logOut: true
            })
            res.status(StatusCodes.OK).json({
                message: "Visitor checked out",
                status: StatusCodes.OK,
                success: true,
            })
        }else{
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "Visitor not found. Please check your phone number",
                status: StatusCodes.BAD_REQUEST,
                success: true,
            })
        }
    } catch (error) {
        console.log(error);
        res.status(500).json({ error: error.message });
    }


}

export default checkOut;

