sourcesContent:
import { visitorModel } from "../middleware/dbConnect";
import { sendOTP } from "../utills/otp";
import { StatusCodes } from "http-status-codes";



const bookWalkIn = async (req, res) => {
    const { name, email, company, phoneNo, visitingPurpose } = req.body;
    let option = {
        phoneNo: phoneNo,
        visitingDate: new Date(),
    };

    try {
        const checkVisitor = await visitorModel.find(option).exec();
        if (checkVisitor.length > 0) {
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "Visitor already exists",
                status: StatusCodes.BAD_REQUEST,
                success: true,
            })
        }
        // save walk-in visitor info in the database for the day
        else {
            await new visitorModel({
                name: name,
                email: email,
                company: company,
                phoneNo: phoneNo,
                visitingPurpose: visitingPurpose,
                visitingDate: new Date(),
                checkInTime: new Date().toTimeString(),
            }).save();
            //send otp
            sendOTP(phoneNo).then(async (verificationCode) => {
                res.status(StatusCodes.OK).json({
                    status: StatusCodes.OK,
                    success: true,
                    serviceSid: verificationCode.serviceSid,
                    phoneNo: verificationCode.to,
                })
            })
        }

    } catch (error) {
        console.log(error);
        res.status(500).json({ error: error.message });
    }
}

export default bookWalkIn;

