sourcesContent:
import { visitorModel } from "../middleware/dbConnect";
import { sendOTP } from "../utills/otp";
import { StatusCodes  } from "http-status-codes";


// check if visitor has been pre-booked or not

const checkNumber = async (req, res) => {

    const { name, email, company, phoneNo, visitingPurpose } = req.body;
    let option = {
        phoneNo: phoneNo,
        visitorStatus: "Pre-booked",
        visitingDate: new Date().toDateString(),
      };

    try {
        //check if visitor is prebooked
        const checkVisitor = await visitorModel.find(option).exec();

        if(checkVisitor.length > 0){

            console.log(checkVisitor)
            sendOTP(phoneNo).then((verificationCode) => {
                res.status(StatusCodes.OK).json({
                    message: "SMS sent",
                    status: StatusCodes.OK,
                    success: true,
                    data: {
                        sid: verificationCode.serviceSid,
                        phoneNo: verificationCode.to,
                    }
                })
            })
        }else{
            // check if walkin visitor is already in the database for the day
            option.visitorStatus = "Walk-in";
            const checkVisitor = await visitorModel.find(option).exec();
            if(checkVisitor.length > 0){
                res.status(StatusCodes.BAD_REQUEST).json({
                    message: "Visitor already exists",
                    status: StatusCodes.BAD_REQUEST,
                    success: true,
                })
            }else{

                res.status(StatusCodes.NOT_FOUND).json({
                    message: "Visitor has not been pre-booked",
                    status: StatusCodes.NOT_FOUND,
                    success: true,
                })

                // await new visitorModel({
                //     name: name, 
                //     email: email,
                //     company: company,
                //     phoneNo : phoneNo,
                //     visitingPurpose: visitingPurpose,
                //     visitingDate: new Date().toDateString(),
                //     checkInTime: new Date().toTimeString(),
                // }).save();
                // //send otp
                // sendOTP(phoneNo).then(async(verificationCode) => {
                //     res.status(statusCode.OK).json({
                //         status: statusCode.OK,
                //         success: true,
                //         serviceSid: verificationCode.serviceSid,
                //         phoneNo: verificationCode.to,
                //     })
                // })
            }
        }       

    } catch (error) {
        console.log(error);
        res.status(500).json({ error: error.message });
    }
}

export default checkNumber;

