sourcesContent:
import { visitorModel } from "../middleware/dbConnect";
import { StatusCodes } from "http-status-codes";

const getCheckIns = async (req, res) => {

    //gegt all visitor by company
    const user = req.user;

    let findOption = {
        companyId: user.id,
        visitingDate: new Date().toDateString()
    };

    let option = {
        page: req.query.page || 1,
        limit: req.query.limit || process.env.PAGING_LIMIT,
        sort: { _id: -1 },
    };

    try {
        const checkIns = await visitorModel.paginate( findOption, option);

        if(checkIns){
            res.status(StatusCodes.OK).json({
                message: "Check-ins retrieved successfully",
                success: true,
                checkIns: checkIns,
            })
        }else{
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "No check-ins found",
                success: false,
            })
        }
    } catch (error) {
        console.log(error);
        res.status(500).json({ error: error.message });
    }
}

export default getCheckIns;

