import express from 'express';

const router = express();


import auth from '../middleware/auth';
import superAdminAuth from '../middleware/superAdminAuth';
import createAdmin from '../controllers/admin/dashboardUsers/createAdmin';
// import getCheckIns from '../controllers/getCheckIns';
import adminLogin from '../controllers/admin/dashboardUsers/adminLogin';
import adminProfile from '../controllers/admin/dashboardUsers/adminProfile';





router.post('/createAdmin', auth, superAdminAuth, createAdmin);
// router.get('/fetchCheckIns', auth, getCheckIns);
router.post('/login', adminLogin);
router.get('/view_Profile', auth, adminProfile);






module.exports = router;

