sourcesContent:
import { staffModel } from "../../../middleware/dbConnect";
import { StatusCodes } from "http-status-codes";

const addStaff = async (req, res) => {
    const { name, email, phoneNo, role, department } = req.body;
    const companyId = req.user.id;
    const companyName = req.user.company;

    try {

        const genNo = await staffModel.find({ companyId: companyId }).countDocuments() + 1;
        const seconds = new Date().getSeconds().toString();
        const staffId = `${companyName}/0${seconds}/0${genNo}`;


        //check if staff already exisits
        const checkStaff = await staffModel.find({ email: email}).exec();
        if (checkStaff.length > 0) {
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "Staff has already been created",
                status: StatusCodes.BAD_REQUEST,
                success: true,
            })
            return;
        }
        else {
            // insert data into the db
           const newStaff = await new staffModel({
                name: name,
                email: email,
                companyId: companyId,
                roleId: role,
                departmentId: department,
                staffId: staffId,
                phoneNo: phoneNo
            }).save();

            res.status(StatusCodes.OK).json({
                message: "Staff has been created successfully",
                status: StatusCodes.OK,
                success: true,
                data: newStaff
            })
        }
    } catch (error) {
        console.log(error);
        res.status(500).json({ error: error.message });
    }
}

export default addStaff;

