sourcesContent:
import express from "express";

import auth from "../middleware/auth";
import addDepartment from "../controllers/admin/departments/addDepartments";
import { getAllDepartments, getDepartment } from "../controllers/admin/departments/getDepartments";
import deleteDepartment from "../controllers/admin/departments/deleteDepartment";
import updateDepartment from "../controllers/admin/departments/updateDepartment";


const router = express.Router();

router.post("/addDepartment", auth, addDepartment);
router.get("/getAllDepartments", auth, getAllDepartments)
router.get("/getDepartment/:id", auth, getDepartment)
router.patch("/updateDepartment/:id", auth, updateDepartment)
router.delete("/deleteDepartment/:id", auth, deleteDepartment)

module.exports = router;

