sourcesContent:
import { departmentsModel } from "../../../middleware/dbConnect";
import { StatusCodes } from "http-status-codes";

const addDepartments = async (req, res) => {
    const { department } = req.body;
    const companyId = req.user.id;
    //change first letter to uppercase of each word 
    const departmentName = department.replace(/\w\S*/g, function(txt){return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();});

    let option = {
        department: departmentName,
        companyId: companyId,
        isDeleted: false

        };

    try {
        //check if department already exists
        const checkDepartment = await departmentsModel.find(option).exec();
        if (checkDepartment.length > 0) {
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "Department has already been created",
                status: StatusCodes.BAD_REQUEST,
                success: false,
            })
            return;
        }
        else {
            // insert data into the db
            const newDepartment = await new departmentsModel({
                department: departmentName,
                companyId: companyId
            }).save();

            res.status(StatusCodes.OK).json({
                message: "Department has been created successfully",
                status: StatusCodes.OK,
                success: true,
                data: newDepartment
            })
        }
    } catch (error) {
        res.status(StatusCodes.INTERNAL_SERVER_ERROR).json({ error: error.message });
    }
}

export default addDepartments;

