sourcesContent:
import { departmentsModel } from "../../../middleware/dbConnect";
import { StatusCodes } from "http-status-codes";


const getAllDepartments = async (req, res) => {
    let option = {
        page: req.query.page || 1,
        limit: req.query.limit || process.env.PAGING_LIMIT,
        sort: { _id: -1 },
    };
    try {
        let findOption = {
            companyId: req.user.id,
            isDeleted: false
        };
        const departments = await departmentsModel.paginate(findOption, option);
        if (departments.length < 1) {
            res.status(StatusCodes.OK).json({
                message: "Departments retrieved successfully",
                success: true,
                departments: departments,
            })
        } else {
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "No departments found",
                success: false,
            })
        }
    } catch (error) {
        console.log(error);
        res.status(500).json({ error: error.message });
    }
}


const getDepartment = async (req, res) => {
    let option = {
        _id: req.params.id,
        companyId: req.user.id,
        isDeleted: false
    };
    try {
        const department = await departmentsModel.find(option).exec();
        if (department.length > 0) {
            res.status(StatusCodes.OK).json({
                message: "Department retrieved successfully",
                status: StatusCodes.OK,
                success: true,
                department: department,
            });
        } else {
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "Department not found",
                status: StatusCodes.BAD_REQUEST,
                success: false,
            });
        }
    } catch (error) {
        console.log(error);
        res.status(500).json({ error: error.message });
    }
}


module.exports = { getAllDepartments, getDepartment };

