sourcesContent:
import express from "express";

import auth from "../middleware/auth";
import addRole from "../controllers/admin/roles/addRole";
import { getAllRoles, getRole } from "../controllers/admin/roles/getRoles";
import deleteRole from "../controllers/admin/roles/deleteRole";
import updateRole from "../controllers/admin/roles/updateRole";

const router = express.Router();

router.post("/addRole", auth, addRole);
router.get("/getAllRoles", auth, getAllRoles)
router.get("/getRole/:id", auth, getRole)
router.delete("/deleteRole/:id", auth, deleteRole)
router.patch("/updateRole/:id", auth, updateRole)

module.exports = router;

