sourcesContent:
import { rolesModel } from "../../../middleware/dbConnect";
import { StatusCodes } from "http-status-codes";

const addRole = async (req, res) => {
    const { role } = req.body;

    const roleName = role.replace(/\w\S*/g, function(txt){return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();});

    let option = {
        role: roleName,
        companyId: req.user.id,
        isDeleted: false
    };

    try {
        const role = await rolesModel.find(option).exec();
        if ( role.length > 0 ) {
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "Role has already been created",
                status: StatusCodes.BAD_REQUEST,
                success: false,
            })
            return;
        }
        else {
            const newRole = await new rolesModel({
                role: roleName,
                companyId: req.user.id
            }).save();

            res.status(StatusCodes.OK).json({
                message: "Role has been created successfully",
                status: StatusCodes.OK,
                success: true,
                data: newRole
            })
        }
    }
    catch (error) {
        res.status(StatusCodes.INTERNAL_SERVER_ERROR).json({ error: error.message });
    }
}


export default addRole;

