import { rolesModel } from "../../../middleware/dbConnect";
import { StatusCodes } from "http-status-codes";


const getAllRoles = async (req, res) => {
    let option = {
        page: req.query.page || 1,
        limit: req.query.limit || process.env.PAGING_LIMIT,
        sort: { _id: -1 },
    };
    try {
        let findOption = {
            companyId: req.user.id,
            isDeleted: false
        };
        const roles = await rolesModel.paginate(findOption, option);
        if (roles) {
            res.status(StatusCodes.OK).json({
                message: "roles retrieved successfully",
                success: true,
                roles: roles,
            })
        } else {
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "No roles found",
                success: false,
            })
        }
    } catch (error) {
        console.log(error);
        res.status(500).json({ error: error.message });
    }
}


const getRole = async (req, res) => {
    let option = {
        _id: req.params.id,
        companyId: req.user.id,
        isDeleted: false
    };
    console.log(option);
    try {
        const role = await rolesModel.find(option).exec();
        if (role.length > 0) {
            res.status(StatusCodes.OK).json({
                message: "role retrieved successfully",
                status: StatusCodes.OK,
                success: true,
                role: role,
            });
        } else {
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "role not found",
                status: StatusCodes.BAD_REQUEST,
                success: false,
            });
        }
    } catch (error) {
        console.log(error);
        res.status(500).json({ error: error.message });
    }
}


module.exports = { getAllRoles, getRole };

