sourcesContent:
import { employeeModel } from "../../../middleware/dbConnect";
import { StatusCodes } from "http-status-codes";

const addEmployee = async (req, res) => {
    const { name, email, phoneNo, roleId, departmentId } = req.body;
    const companyId = req.user.id;
    const companyName = req.user.company;

    try {

        const genNo = await employeeModel.find({ companyId: companyId }).countDocuments() + 1;
        const seconds = new Date().getSeconds().toString();
        const employeeId = `${companyName}/0${seconds}/0${genNo}`;


        //check if employee already exisits
        const checkemployee = await employeeModel.find({ email: email}).exec();
        if (checkemployee.length > 0) {
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "Employee has already been created",
                status: StatusCodes.BAD_REQUEST,
                success: true,
            })
            return;
        }
        else {
            // insert data into the db
           const newemployee = await new employeeModel({
                name: name,
                email: email,
                companyId: companyId,
                roleId: roleId,
                departmentId: departmentId,
                employeeId: employeeId,
                phoneNo: phoneNo
            }).save();

            res.status(StatusCodes.OK).json({
                message: "Employee has been created successfully",
                status: StatusCodes.OK,
                success: true,
                data: newemployee
            })
        }
    } catch (error) {
        console.log(error);
        res.status(500).json({ error: error.message });
    }
}

export default addEmployee;

