var mongoosePaginate = require("mongoose-paginate-v2");
var Schema = mongoose.Schema;
var employeeSchema = new Schema({
  name: {
    type: String,
    required: true
  },
  email: {
    type: String,
    required: true
  },
  companyId: {
    type: String,
    required: true
  },
  phoneNo: {
    type: String,
    required: true
  },
  employeeId: {
    type: String,
    required: true
  },
  roleId: {
    type: Schema.Types.ObjectId,
    ref: "roles",
    required: true
  },
  departmentId: {
    type: Schema.Types.ObjectId,
    ref: "departments",
    required: true
  },
  isDeleted: {
    type: Boolean,
    "default": false
  },
  createdAt: {
    type: Date,
