const mongoose = require("mongoose");
const mongoosePaginate = require("mongoose-paginate-v2");
const Schema = mongoose.Schema

const employeeSchema = new Schema({
    name:{
        type: String,
        required: true
    },
    email:{
        type: String,
        required: true
    },
    companyId:{
        type: String,
        required: true
    },
    phoneNo: {
        type: String,
        required: true 
    },
    employeeId: {
        type: String,
        required: true
    },
    roleId: {
        type: Schema.Types.ObjectId,
        ref: "roles",
        required: true
    },
    departmentId: {
        type: Schema.Types.ObjectId,
        ref: "departments",
        required: true
    },

    isDeleted: { 
        type: Boolean, 
        default: false 
    },
    createdAt: { 
        type: Date, 
        default: new Date() 
    },
    updatedAt: { 
        type: Date, 
        default: new Date() 
    },
    deletedAt: Date
    },

);

employeeSchema.plugin(mongoosePaginate);

module.exports = employeeSchema;

