sourcesContent:
import express from 'express';

import auth from '../middleware/auth';
import addEmployee from "../controllers/admin/employee/addEmployee";
import { getAllEmployees, getEmployee } from "../controllers/admin/employee/getEmployees";
import deleteEmployee from "../controllers/admin/employee/deleteEmployee";
import updateEmployee from "../controllers/admin/employee/updateEmployee";
import searchEmployees from "../controllers/admin/employee/searchEmployees";

const router = express.Router();



router.post('/addEmployee', auth, addEmployee);
router.get('/getAllEmployee', auth, getAllEmployees);
router.get('/getEmployee/:id', auth, getEmployee);
router.delete('/deleteEmployee/:id', auth, deleteEmployee);
router.patch('/updateEmployee/:id', auth, updateEmployee);
router.get('/searchEmployees', auth, searchEmployees);



module.exports = router;

