import { employeeModel } from "../../../middleware/dbConnect";
import { StatusCodes } from "http-status-codes";


const getAllEmployees = async (req, res) => {
    let option = {
        page: req.query.page || 1,
        limit: req.query.limit || process.env.PAGING_LIMIT,
        sort: { _id: -1 },
        populate: [{
            path: "departmentId",
            select: "department",
        },{
            path: "roleId",
            select: "role",
        }
    ],


    };
    try {
        let findOption = {
            companyId: req.user.id,
            isDeleted: false
        };
        const employee = await employeeModel.paginate(findOption, option);
        if (employee) {
            res.status(StatusCodes.OK).json({
                message: "employee retrieved successfully",
                success: true,
                employee: employee,
            })
        } else {
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "No employee found",
                success: false,
            })
        }
    } catch (error) {
        console.log(error);
        res.status(500).json({ error: error.message });
    }
}




const getEmployee = async (req, res) => {
    let FindOptions = {
        companyId: req.user.id,
        _id: req.params.id,
        isDeleted: false,
    };
    try {
        const employee = await employeeModel.findOne(FindOptions).populate("departmentId", 'department').populate("roleId", 'role').exec();
        if (employee) {
            res.status(StatusCodes.OK).json({
                message: "employee retrieved successfully",
                status: StatusCodes.OK,
                success: true,
                employee: employee,
            });
        }
        else {
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "employee not found",
                status: StatusCodes.BAD_REQUEST,
                success: false,
            });
        }
    } catch (error) {
        res.status(500).json({ error: error.message });
    }
}


module.exports = { getAllEmployees, getEmployee };

