sourcesContent:
import { employeeModel } from "../../../middleware/dbConnect";
import { StatusCodes } from "http-status-codes";

const updateEmployee = async (req, res) => {
    const { id } = req.params;
    const companyId = req.user.id;
    const { name, email, phoneNo, roleId, departmentId } = req.body;
    try {
        let option = {
            _id: id,
            companyId: companyId,
            isDeleted: false
        };
        const employee = await employeeModel.findOneAndUpdate(option,
            { name: name, email: email, phoneNo: phoneNo, roleId: roleId, departmentId: departmentId }, { new: true }).exec();
        if (employee) {
            res.status(StatusCodes.OK).json({
                message: "employee updated successfully",
                status: StatusCodes.OK,
                success: true,
                data: employee,
            })
        }
        else {
            res.status(StatusCodes.BAD_REQUEST).json({
                message: "employee not found",
                status: StatusCodes.BAD_REQUEST,
                success: false,

            })
        }
    } catch (error) {
        res.status(500).json({ error: error.message });
    }
}


export default updateEmployee;

