sourcesContent:
import { employeeModel } from "../../../middleware/dbConnect";
import { StatusCodes } from "http-status-codes";


//search exact match
const searchEmployees = async (req, res) => {
    let option = {
        page: req.query.page || 1,
        limit: req.query.limit || process.env.PAGING_LIMIT,
        sort: { _id: -1 },
        populate: [{
            path: "departmentId",
            select: "department",
        },{
            path: "roleId",
            select: "role",
        }
    ],

    };
    try {
        let findOption = {
            companyId: req.user.id,
            isDeleted: false
        };
        if (req.query.name) {
            findOption.name = req.query.name;
        }
        if (req.query.email) {
            findOption.email = req.query.email;
        }
        if (req.query.phoneNo) {
            findOption.phoneNo = req.query.phoneNo;
        }
        const employee = await employeeModel.paginate(findOption, option);
        if (employee.docs.length > 0) {
            res.status(StatusCodes.OK).json({
                message: "employee retrieved successfully",
                success: true,
                employee: employee,
            })

        } else {
            res.status(StatusCodes.BAD_REQUEST).json({

                message: "No employee found",

                success: false,

            })

        }

    } catch (error) {

        console.log(error);

        res.status(500).json({ error: error.message });

    }

}

export default searchEmployees;

